mapper.update:
update outlay_alarm
actual_id = #{actualId}, budget_id = #{budgetId}, setting_id = #{settingId}, setting_ratio = #{settingRatio}, cause = #{cause}, beyond_ratio = #{beyondRatio}, remark = #{remark}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, create_by = #{createBy},
where alarm_id = #{alarmId}

mapper.select:
SELECT COUNT(a.alarm_id) countTotal,CONCAT(YEAR(a.create_time),'_',quarter(a.create_time)) qu,
quarter(a.create_time)AS `quarter`
FROM `outlay_alarm` a LEFT JOIN outlay_budget bu ON  a.budget_id = bu.budget_id


        AND budget_type = #{budgetType}


        AND YEAR(a.create_time) = #{year}


GROUP BY qu

SELECT a.alarm_id,b.`year`,b.budget_type as budgetType,b.title,b.amount AS forecastAmount,ac.amount AS actualAmount,a.setting_ratio,a.beyond_ratio,a.create_time
FROM  outlay_alarm a
          LEFT JOIN outlay_actual ac ON ac.actual_id = a.actual_id
          LEFT JOIN outlay_budget b ON b.budget_id = a.budget_id

     and b.title = #{title}
     and b.project_number = #{projectNumber}
     and b.budget_type = #{budgetType}

        and DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%S')


        and DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')
