mapper.update:
update feature_double_work
double_type = #{doubleType}, title = #{title}, status = #{status}, content = #{content}, pic_urls = #{picUrls}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, submit_user_id = #{submitUserId}, receive_user_id = #{receiveUserId}, is_read = #{isRead}, rate = #{rate}, address = #{address}, location = #{location},
where double_id = #{doubleId}

mapper.select:
select w.double_id, w.double_type, w.title, w.status, w.content, w.pic_urls, w.create_by, w.create_time, w.update_by, w.update_time, w.remark, w.submit_user_id, w.receive_user_id,
w.is_read,u.avatar,u.nick_name AS nickName,u.phonenumber from feature_double_work w LEFT JOIN sys_user u ON u.user_id = w.receive_user_id

     and w.double_type = #{doubleType}
     and w.title = #{title}
     and w.status = #{status}
     and w.content = #{content}
     and w.pic_urls = #{picUrls}
     and w.submit_user_id = #{submitUserId}
     and w.receive_user_id = #{receiveUserId}
     and w.is_read = #{isRead}
    and w.rate = #{rate},

select double_id, double_type, title, status, content, pic_urls, create_by, create_time, update_by, update_time, remark, submit_user_id, receive_user_id, is_read,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='double_type' AND d.dict_value = a.double_type )AS doubleTypeName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='double_status' AND d.dict_value = a.status )AS doubleStatusName,
(SELECT u.nick_name FROM sys_user u WHERE u.user_id = a.submit_user_id)AS sendUserName,
(SELECT u.nick_name FROM sys_user u WHERE u.user_id = a.receive_user_id)AS receiveUserName
from feature_double_work a

     and double_type = #{doubleType}
     and a.title = #{title}
     and status = #{status}
     and content = #{content}
     and pic_urls = #{picUrls}
     and submit_user_id = #{submitUserId}
     and receive_user_id = #{receiveUserId}
     and is_read = #{isRead}
