mapper.update:
update feature_double_work
type = #{type}, title = #{title}, status = #{status}, pic_urls = #{picUrls}, content = #{content}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, receive_user_id = #{receiveUserId}, send_user_id = #{sendUserId}, rate = #{rate}, address = #{address}, location = #{location}, process_type = #{processType}, nick_name = #{nickName}, phonenumber = #{phonenumber}, id_card = #{idCard}, home_address = #{homeAddress}, station_id = #{stationId},
where double_id = #{doubleId}

mapper.select:
select w.double_id, w.type, w.title, w.status, w.content, w.pic_urls, w.create_by, w.create_time,
       w.update_by, w.update_time, w.remark, w.send_user_id, w.receive_user_id,
       w.rate,w.location,w.process_type,
        u.avatar,u.nick_name AS nickName,u.phonenumber as receivePhone,u2.phonenumber as sendPhone,
        (SELECT u.nick_name FROM sys_user u WHERE u.user_id = w.receive_user_id)AS receiveName
from feature_double_work w
    LEFT JOIN sys_user u ON u.user_id = w.receive_user_id
    LEFT JOIN sys_user u2 ON u2.user_id = w.send_user_id

    and w.type = #{type},
     and w.title = #{title}
     and w.status = #{status}
     and w.content = #{content}
     and w.pic_urls = #{picUrls}
    and w.send_user_id = #{sendUserId}
     and w.receive_user_id = #{receiveUserId}
    and w.rate = #{rate}

select double_id, type, title, status, content, pic_urls, create_by, create_time, update_by, update_time, remark, send_user_id,
       receive_user_id,a.rate,a.location,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='double_type' AND d.dict_value = a.type )AS doubleTypeName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='double_status' AND d.dict_value = a.status )AS doubleStatusName,
(SELECT u.nick_name FROM sys_user u WHERE u.user_id = a.send_user_id)AS sendUserName,
(SELECT u.nick_name FROM sys_user u WHERE u.user_id = a.receive_user_id)AS receiveUserName
from feature_double_work a

    and a.type = #{type},
     and a.title = #{title}
     and status = #{status}
     and content = #{content}
     and pic_urls = #{picUrls}
    and w.send_user_id = #{sendUserId},
     and receive_user_id = #{receiveUserId}
