mapper.select:


     and title = #{title}
     and meeting_type = #{meetingType}
     and start_time = #{startTime}
     and end_time = #{endTime}
     and address = #{address}
     and content = #{content}
     and is_sign = #{isSign}
     and status = #{status}

ORDER BY start_time DESC

mapper.update:
update oa_meeting
title = #{title}, meeting_type = #{meetingType}, start_time = #{startTime}, end_time = #{endTime}, address = #{address}, content = #{content}, is_sign = #{isSign}, status = #{status}, remark = #{remark}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, create_by = #{createBy}, file_list = #{fileList},
where meeting_id = #{meetingId}

mapper.select:
select
meeting_id, title, meeting_type, start_time, end_time, address, content, is_sign, status, remark, create_time, update_by, update_time, create_by,file_list,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='meeting_type' AND d.dict_value = a.meeting_type)AS meetingTypeName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='meeting_status' AND d.dict_value = a.`status`)AS statusName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='meeting_is_sign' AND d.dict_value = a.is_sign)AS isSignName
from oa_meeting a

     and title = #{title}
     and meeting_type = #{meetingType}
     and start_time = #{startTime}
     and end_time = #{endTime}
     and address = #{address}
     and content = #{content}
     and is_sign = #{isSign}
     and a.status = #{status}

ORDER BY start_time DESC
