mapper.update:
update represent_activity
activity_type = #{activityType}, remark = #{remark}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, create_by = #{createBy}, title = #{title}, start_time = #{startTime}, end_time = #{endTime}, address = #{address}, status = #{status}, participate_conut = #{participateConut}, content = #{content},
