mapper.update:
update represent_activity
activity_type = #{activityType}, remark = #{remark}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, create_by = #{createBy}, title = #{title}, start_time = #{startTime}, end_time = #{endTime}, address = #{address}, status = #{status}, participate_conut = #{participateConut}, content = #{content}, npc_claim = #{npcClaim}, open_flag = #{openFlag},
where activity_id = #{activityId}

mapper.select:
select activity_id, activity_type, remark, create_time, update_by, update_time, create_by, title, start_time,
end_time, address, status, participate_conut, content, npc_claim, open_flag,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='activity_categories' AND d.dict_value = a.activity_type
)AS activityTypeName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='activity_status' AND d.dict_value = a.`status` )AS
statusName
from represent_activity a

    and a.activity_type = #{activityType}
    and a.title = #{title}
    and a.start_time = #{startTime}
    and a.end_time = #{endTime}
    and a.address = #{address}
    and a.status = #{status}
    and a.participate_conut = #{participateConut}
    and a.content = #{content}
    and a.npc_claim = #{npcClaim}
    and a.open_flag = #{openFlag}

ORDER BY start_time DESC

SELECT act.activity_id, act.activity_type, act.create_time, act.title, act.start_time, act.end_time, act.address, act.participate_conut, act.content, act.npc_claim, act.open_flag, log.record_id, log.user_id, log.content as feedBackContent, log.submit_time, log.status as feedBackStatus, log.title as feedBackTitle FROM represent_activity AS act LEFT JOIN represent_activity_record AS log ON act.activity_id = log.record_id
and act.npc_claim = '1' and log.status = #{status} and (log.user_id = #{userId} or act.open_flag='1') and act.title = #{title}
ORDER BY act.activity_id DESC

SELECT m.* FROM represent_activity_record p LEFT JOIN represent_activity m ON m.activity_id = p.activity_id

    and p.user_id = #{userId}
    and activity_type = #{activityType}
    and title = #{title}
    and start_time = #{startTime}
    and end_time = #{endTime}
    and address = #{address}
    and status = #{status}
    and participate_conut = #{participateConut}
    and content = #{content}
    and npc_claim = #{npcClaim}
    and open_flag = #{openFlag}
