mapper.update:
update represent_discover
title = #{title}, content = #{content}, discover_type = #{discoverType}, remark = #{remark}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, create_by = #{createBy}, address = #{address}, location = #{location}, reply = #{reply}, rate = #{rate}, send_user_id = #{sendUserId}, receive_user_id = #{receiveUserId}, status = #{status}, process_type = #{processType}, pic_urls = #{picUrls}, station_id = #{stationId},
where discover_id = #{discoverId}

mapper.select:
select d.discover_id,
d.title, d.content,d.pic_urls, d.discover_type, d.remark,
d.create_time, d.update_by, d.update_time, d.create_by, d.address, d.location,
d.status, d.reply, d.rate, d.send_user_id,d.receive_user_id,
(SELECT a.title FROM represent_home_access a WHERE a.access_id = d.station_id)AS accessName,
(SELECT d1.dict_label FROM sys_dict_data d1 WHERE d1.dict_type='discover_status' AND d1.dict_value = d.status)AS processTypeName,
(SELECT d1.dict_label FROM sys_dict_data d1 WHERE d1.dict_type='process_type' AND d1.dict_value = d.process_type)AS processTypeName,
(SELECT d1.dict_label FROM sys_dict_data d1 WHERE d1.dict_type='discover_type' AND d1.dict_value = d.discover_type)AS discoverTypeName,
(SELECT u.nick_name FROM sys_user u WHERE u.user_id = d.send_user_id)AS sendUserName,
(SELECT u.nick_name FROM sys_user u WHERE u.user_id = d.receive_user_id)AS receiveUserName
from represent_discover d

     and title = #{title}
     and content = #{content}
     and discover_type = #{discoverType}
     and address = #{address}
     and location = #{location}
     and status = #{status}
     and reply = #{reply}
     and rate = #{rate}
     and send_user_id = #{sendUserId}
     and receive_user_id = #{receiveUserId}

ORDER BY d.create_time DESC

select discover_id, title, content,pic_urls, discover_type, remark, create_time, update_by, update_time, create_by, address, location, reply, rate, send_user_id, receive_user_id, status, process_type,
(SELECT u.nick_name FROM sys_user u WHERE u.user_id = send_user_id)AS sendUserName,
(SELECT u.nick_name FROM sys_user u WHERE u.user_id = receive_user_id)AS receiveUserName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='discover_process_type' AND d.dict_value = a.process_type)AS processTypeName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='discover_type' AND d.dict_value = a.discover_type)AS discoverTypeName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='discover_status' AND d.dict_value = a.status )AS discoverStatusName
from represent_discover a

     and a.title = #{title}
     and content = #{content}
     and discover_type = #{discoverType}
     and address = #{address}
     and location = #{location}
     and status = #{status}
     and reply = #{reply}
     and rate = #{rate}
     and send_user_id = #{sendUserId}
     and receive_user_id = #{receiveUserId}

ORDER BY a.create_time DESC

select discover_id, title, content,pic_urls, discover_type, remark, create_time, update_by, update_time, create_by, address, location, reply, rate, send_user_id, receive_user_id, status, process_type,
(SELECT u.nick_name FROM sys_user u WHERE u.user_id = send_user_id)AS sendUserName,
(SELECT u.nick_name FROM sys_user u WHERE u.user_id = receive_user_id)AS receiveUserName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='discover_process_type' AND d.dict_value = a.process_type)AS processTypeName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='discover_type' AND d.dict_value = a.discover_type)AS discoverTypeName,
(SELECT d.dict_label FROM sys_dict_data d WHERE d.dict_type='discover_status' AND d.dict_value = a.status )AS discoverStatusName
from represent_discover a

    and a.status != 3
     and station_id = #{stationId}
     and a.title = #{title}
     and content = #{content}
     and discover_type = #{discoverType}
     and address = #{address}
     and location = #{location}
     and status = #{status}
     and reply = #{reply}
     and rate = #{rate}
     and send_user_id = #{sendUserId}
     and receive_user_id = #{receiveUserId}

ORDER BY a.create_time DESC
